/* root style start */
:root {
  --blue: #0655ff;
  --indigo: #6610f2;
  --purple: #6f42c1;
  --pink: #e83e8c;
  --red: #f80066;
  --orange: #ffb82d;
  --yellow: #ffde06;
  --green: #00b247;
  --teal: #20c997;
  --cyan: #17a2b8;
  --white: #fff;
  --gray: #6c757d;
  --gray-dark: #343a40;
  --primary: #600dff;
  --secondary: #6c757d;
  --success: #65cc8c;
  --info: #17a2b8;
  --warning: #ffc64c;
  --danger: #ff4d6b;
  --light: #f3f4f7;
  --dark: #7f8eac;
  --breakpoint-xs: 0;
  --breakpoint-sm: 576px;
  --breakpoint-md: 768px;
  --breakpoint-lg: 992px;
  --breakpoint-xl: 1200px;
  --font-family-sans-serif: "Circular", -apple-system, BlinkMacSystemFont,
    "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif, "Apple Color Emoji",
    "Segoe UI Emoji", "Segoe UI Symbol";
  --font-family-monospace: SFMono-Regular, Menlo, Monaco, Consolas,
    "Liberation Mono", "Courier New", monospace;
  --swiper-navigation-size: 44px;
  --swiper-theme-color: #007aff;
  --animate-duration: 1s;
  --animate-delay: 1s;
  --animate-repeat: 1;
}
/* root style end */
/* custom fonts start */
@font-face {
  font-family: figtree;
  src: url("../fonts/Figtree-normal-400-100.ttf");
}
@font-face {
  font-family: figtree-bold;
  src: url("../fonts/Figtree-normal-400-100.ttf");
  font-weight: bold;
  font-weight: 700;
}
/* custom fonts end */
/* header section start */
.main-hero-container {
  background-color: var(--www-lusha-com-red-oxide, #600dff);
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 0 16px;
}

.main-hero-center-content {
  align-items: center;
  display: flex;
  justify-content: center;
  padding: 0 60px;
}

@media (max-width: 991px) {
  .main-hero-center-content {
    max-width: 100%;
    padding: 0 20px;
  }
}

.main-hero-flex-column {
  max-width: 100%;
  display: flex;
  width: 1232px;
  flex-direction: column;
  justify-content: center;
}

.main-hero-wrap-content {
  justify-content: center;
  align-items: start;
  align-content: flex-start;
  flex-wrap: wrap;
  display: flex;
  flex-direction: column;
}

@media (max-width: 991px) {
  .main-hero-wrap-content {
    max-width: 100%;
    padding-right: 20px;
  }
}

.main-hero-content {
  max-width: 100%;
  align-items: start;
  display: flex;
  width: 616px;
  flex-direction: column;
  justify-content: center;
  padding: 80px 16px;
}

@media (max-width: 991px) {
  .main-hero-content {
    padding-right: 20px;
  }
}

.main-hero-text-wrapper {
  justify-content: space-between;
  display: flex;
  width: 414px;
  max-width: 100%;
  flex-direction: column;
  margin: 61px 0 33px;
}

@media (max-width: 991px) {
  .main-hero-text-wrapper {
    margin-top: 40px;
  }
}

.main-hero-heading {
  padding-bottom: 24px;
  display: flex;
  flex-direction: column;
  font-size: 62px;
  color: var(--www-lusha-com-nero, #fff);
  font-weight: 900;
  letter-spacing: -3.2px;
  line-height: 64px;
}

@media (max-width: 991px) {
  .main-hero-heading {
    font-size: 40px;
    line-height: 46px;
  }
}

.main-hero-heading-text {
  font-family: Segoe UI, sans-serif;
  justify-content: center;
}

@media (max-width: 991px) {
  .main-hero-heading-text {
    font-size: 40px;
    line-height: 46px;
  }
}

.main-hero-subheading {
  padding-bottom: 32px;
  display: flex;
  flex-direction: column;
  font-size: 20px;
  color: var(--www-lusha-com-nero, #fff);
  font-weight: 400;
  line-height: 30px;
}

.main-hero-subheading-text {
  font-family: Segoe UI, sans-serif;
  justify-content: center;
}

.main-hero-form-container {
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.main-hero-form-wrapper {
  border-radius: 4px;
  background-color: var(--www-lusha-com-nero, #fff);
  display: flex;
  width: 100%;
  flex-direction: column;
  justify-content: center;
  padding: 3px;
}

.main-hero-form-content {
  align-content: center;
  flex-wrap: wrap;
  display: flex;
  gap: 0px;
}

.main-hero-email-label {
  border-radius: 4px;
  background-color: var(--www-lusha-com-nero, #fff);
  align-self: start;
  display: flex;
  flex-direction: column;
  font-size: 14px;
  color: var(--www-lusha-com-pale-sky, #6c757d);
  justify-content: center;
  flex: 1;
  padding: 10px 16px;
}

.main-hero-email-label-text {
  font-family: Segoe UI, sans-serif;
  justify-content: center;
}

.main-hero-submit-btn {
  justify-content: center;
  border-radius: 4px;
  background-color: var(--www-lusha-com-green, #ffde06);
  color: var(--www-lusha-com-black, #000);
  text-align: center;
  padding: 2px 21px;
  font: 16px/36px Segoe UI, sans-serif;
}

@media (max-width: 991px) {
  .main-hero-submit-btn {
    padding: 0 20px;
  }
}

.main-hero-image-section {
  padding-top: 16px;
  display: flex;
  flex-direction: column;
}

.main-hero-main-image-container {
  justify-content: center;
  align-items: center;
  display: flex;
  padding-top: 2px;
}

.main-hero-main-image {
  aspect-ratio: 16.67;
  object-fit: auto;
  object-position: center;
  width: 100%;
  max-width: 584px;
}

.main-hero-logos-section {
  padding-top: 80px;
  display: flex;
  flex-direction: column;
}

.main-hero-logos-container {
  align-items: start;
  display: flex;
  margin-top: 8px;
  padding-top: 3px;
  gap: 16px;
}

.main-hero-logo-image {
  aspect-ratio: 7.14;
  object-fit: auto;
  object-position: center;
  width: 139px;
  max-width: 100%;
}

.main-hero-logo-image.main-hero-second {
  aspect-ratio: 6.67;
}

.main-hero-logo-image.main-hero-third {
  aspect-ratio: 5.26;
  width: 105px;
}
/* header section end */
