  box-shadow: 0px 1px 6px 0px rgba(0, 0, 0, 0.06),
    0px 2px 32px 0px rgba(0, 0, 0, 0.16);
  align-self: end;
  margin-top: 27px;
}
/* footer end */
/* nav */
.navbar {
  border-radius: 100px;
  box-shadow: 0 10px 20px rgba(56, 58, 66, 0.08);
  border: 1px solid #f1f3fd;
  background-color: #fff;
  display: flex;
  gap: 20px;
  font-weight: 400;
  letter-spacing: 0.16px;
  justify-content: space-between;
  padding: 0 21px;
  margin-top: 20px;
  height: 72px;
}

@media (max-width: 991px) {
  .navbar {
    flex-wrap: wrap;
    padding: 0 20px;
  }
}

.nav-left {
  display: flex;
  gap: 20px;
  color: #000;
  justify-content: space-between;
}

@media (max-width: 991px) {
  .nav-left {
    max-width: 100%;
    flex-wrap: wrap;
  }
}

.logo-container {
  display: flex;
  gap: 14px;
}

.logo {
  aspect-ratio: 1.85;
  object-fit: auto;
  object-position: center;
  width: 130px;
  max-width: 100%;
}

.nav-item {
  display: flex;
  flex-direction: column;
  align-items: start;
  font-size: 15px;
  justify-content: center;
  padding: 28px 14px;
  position: relative;
  display: inline-block;
}

@media (max-width: 991px) {
  .nav-item {
    padding-right: 20px;
  }
}

.nav-link {
  font-family: figtree, sans-serif;
  border-radius: 4px;
  justify-content: center;
  text-decoration: none;
  color: #000333;
}

.nav-text {
  border-radius: 4px;
  justify-content: center;
  margin: auto 0;
  font: 14px figtree, sans-serif;
}

.nav-center {
  display: flex;
  gap: 14px;
  font-size: 14px;
  white-space: nowrap;
}

@media (max-width: 991px) {
  .nav-center {
    white-space: initial;
  }
}

.nav-right {
  display: flex;
  gap: 15px;
  font-size: 14px;
  margin: auto 0;
}

.btn {
  font-family: figtree, sans-serif;
  justify-content: center;
  border-radius: 30px;
  padding: 15px 24px;
  text-decoration: none;
}

@media (max-width: 991px) {
  .btn {
    padding: 0 20px;
  }
}

.btn-outline {
  border: 1px solid #000333;
  background-color: #fff;
  color: #000333;
}

.btn-solid {
  background-color: #000333;
  color: #fff;
  padding: 16px 25px;
}

.visually-hidden {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}
.sub-items-div-data-os {
  position: absolute;
  margin-top: 27px;
  left: 0;
  display: none;
  background-color: #ffffff;
  box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
  border-radius: 10px;
  padding: 15px;
  z-index: 1;
}

.nav-item:hover .sub-items-div-data-os {
  display: block;
}
.data-os-brdr:hover {
  border-bottom: 4px solid rgb(38, 72, 247);
  border-radius: 4px;
}

/* nav end */
.sub-menu-design-temp {
  width: 100%;
}
.submenu-design-tem {
  background-color: #ffffff;
  box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
